import React, { useEffect } from "react";
import axios from "axios";

function Gallery() {
  const URL =
    "https://api.airtable.com/v0/appgSipibWEhbQcAf/userEdits&filterByFormula=fields.imageID='recogH9MefyNngjMB'";
  const query = { filterByFormula: '({imageID}="recogH9MefyNngjMB")' };
  useEffect(() => {
    const getImg = async () => {
      const data = await axios.get(URL, {
        headers: {
          Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
        },
      });
      console.log(data);
    };
    getImg();
  }, []);

  return <div></div>;
}

export default Gallery;
