import React, { useEffect, useState } from "react";
import Photo from "./Photo";
import Posts from "./Posts";
import Editor from "./Editor";
import axios from "axios";
import { useParams } from "react-router";

function Collaborator() {
  const { imageID } = useParams();

  const [gray, updGray] = useState(0);
  const [sepia, updSepia] = useState(0);
  const [hue, updHue] = useState(0);
  const [bright, updBright] = useState(1);
  const [ctrast, updCtrast] = useState(1);
  const [satur, updSatur] = useState(1);
  const [newPost, updNewPost] = useState(false);
  const [email, updEmail] = useState("");
  const [notes, updNotes] = useState("");

  const handleSubmit = async (e) => {
    //const URL = "https://api.airtable.com/v0/appgSipibWEhbQcAf/userEdits";
    e.preventDefault();
    console.log(bright);
    try {
      const post = await axios.post(
        "https://api.airtable.com/v0/appgSipibWEhbQcAf/userEdits",
        {
          fields: {
            user_email: email,
            imageID: imageID,
            notes: notes,
            brightness: 1.5,
          },
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      );
      updNewPost(!newPost);
      updNotes("");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="collab">
      <Photo
        gray={gray}
        //updGray={updGray}
        sepia={sepia}
        //updSepia={updSepia}
        hue={hue}
        //updHue={updHue}
        bright={bright}
        //updBright={updBright}
        ctrast={ctrast}
        //updCtrast={updCtrast}
        satur={satur}
        //updSatur={updSatur}
      />
      <Posts newPost={newPost} />
      <Editor
        gray={gray}
        updGray={updGray}
        sepia={sepia}
        updSepia={updSepia}
        hue={hue}
        updHue={updHue}
        bright={bright}
        updBright={updBright}
        ctrast={ctrast}
        updCtrast={updCtrast}
        satur={satur}
        updSatur={updSatur}
        email={email}
        updEmail={updEmail}
        notes={notes}
        updNotes={updNotes}
        newPost={newPost}
        updNewPost={updNewPost}
        handleSubmit={handleSubmit}
      />
    </div>
  );
}

export default Collaborator;
