import React, { useEffect, useState } from "react";

function Editor(props) {
  return (
    <div>
      <label htmlFor="grayscale">
        Grayscale
        <input
          name="grayscale"
          id="grayscale"
          type="range"
          min="0"
          max="1"
          steps="0.01"
          data-unit=""
          value={props.grayscale}
          onChange={(e) => {
            props.updateGrayscale(e.target.value);
          }}
        />
      </label>
    </div>
  );
}

export default Editor;
