  };

  return (
    <>
      {posts &&
        posts.map((post) => {
          return (
            <div key={post.id}>
              <p onClick={displayPostEdits}>
                {post.fields.user_email}
                <br />
                {post.fields.notes}
              </p>
            </div>
          );
        })}
    </>
  );
}

export default Posts;
